import { Component, OnInit, ChangeDetectorRef } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.css']
})
export class OrdersComponent implements OnInit {
  orders: any[];

  constructor(
    private authService: AuthService, 
    private changeDetectorRef: ChangeDetectorRef // Inject ChangeDetectorRef
  ) {}

  ngOnInit() {
    this.reloadComponent();
  }

  reloadComponent() {
    this.orders = this.authService.loadOrders();
  }

  calculateTotal(order: any) {
    return order.items.reduce((total: number, item: any) => total + parseFloat(item.price), 0);
  }

  deleteOrder(order: any) {
    this.orders = this.orders.filter(orderItem => orderItem._id !== order._id);
    this.authService.deleteOrder(order);
    setTimeout(() => {this.reloadComponent(); }, 1000);
    
    this.changeDetectorRef.detectChanges(); // Trigger change detection
  }
}
<app-menu-bar></app-menu-bar>
<h2>Orders</h2>
<div *ngIf="orders.length > 0">
    <h3>List of Orders</h3>
    <ul>
        <div *ngFor="let order of orders">
            <div>
                <br/>
                <h3>{{ order.user.username }} - {{ order._id }}</h3> 
                <li *ngFor="let item of order.items">
                    {{ item.name }} - {{ item.price }}
                </li> 
                <h4>Total: ${{ calculateTotal(order) }}</h4>
                <button (click)="deleteOrder(order)">Delete Order</button>  
            </div>
        </div>
    </ul>
</div>
