import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { AppConfig } from './config';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  private apiUrl = 'https://smooth-comfort-405104.uc.r.appspot.com/document/findAll/';
  private userUrl = this.apiUrl + 'userx';
  private catalogUrl = this.apiUrl + 'catalog';
  private ordersUrl = this.apiUrl + 'orders';
  constructor(private http: HttpClient) {}

  getAllUsers(): Observable<any> {
    const headers = new HttpHeaders().set('Authorization',AppConfig.apiKey);
    return this.http.get(this.userUrl, {headers});
  }
  getCatalog(): Observable<any> {
    const headers = new HttpHeaders().set('Authorization',AppConfig.apiKey);
    return this.http.get(this.catalogUrl, {headers});
  }
  getOrders(): Observable<any> {
    const headers = new HttpHeaders().set('Authorization',AppConfig.apiKey);
    return this.http.get(this.ordersUrl, {headers});
  }
  postUser(username: string, password: string): Observable<any> {
    const headers = new HttpHeaders().set('Authorization',AppConfig.apiKey);
    return this.http.post(this.userUrl, { "username":username, "password":password }, {headers});
  }
}
